import React, { Component } from 'react'
import EmployeeService from '../services/EmployeeService'

class ListEmployeeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            employees: []
        }
        this.addEmployee = this.addEmployee.bind(this);
        this.editEmployee = this.editEmployee.bind(this);
        this.deleteEmployee = this.deleteEmployee.bind(this);
    }

    deleteEmployee(id) {
        EmployeeService.deleteEmployee(id).then(res => {
            this.setState({employees: this.state.employees.filter(employee => employee.id !== id)});
        });
    }

    viewEmployee(id) {
        this.props.history.push(`/view-employee/${id}`);
    }

    editEmployee(id) {
        this.props.history.push(`/add-employee/${id}`);
    }

    componentDidMount() {
        EmployeeService.getEmployees().then((res) => {
            this.setState({employees: res.data});
        });
    }

    addEmployee() {
        this.props.history.push('/add-employee/_add');
    }

    render() {
        return (
            <div className="container employees-page">
                <div className="employees-header">
                    <div>
                        <h2 className="employees-header-title">Employees</h2>
                        <p className="employees-header-subtitle">
                            Manage your team members and their contact information.
                        </p>
                    </div>
                    <button
                        className="btn btn-primary shadow-sm"
                        onClick={this.addEmployee}
                    >
                        + Add Employee
                    </button>
                </div>

                <div className="card employees-card">
                    <div className="card-body">
                        <table className="table table-hover employees-table">
                            <thead>
                            <tr>
                                <th>First Name</th>
                                <th>Last Name</th>
                                <th>Email ID</th>
                                <th className="text-end">Actions</th>
                            </tr>
                            </thead>
                            <tbody>
                            {this.state.employees.map(employee => (
                                <tr key={employee.id}>
                                    <td>{employee.firstName}</td>
                                    <td>{employee.lastName}</td>
                                    <td>{employee.emailId}</td>
                                    <td className="text-end">
                                        <button
                                            onClick={() =>
                                                this.editEmployee(employee.id)
                                            }
                                            className="btn btn-info me-2"
                                        >
                                            Update
                                        </button>
                                        <button
                                            style={{marginRight: '0.5rem'}}
                                            onClick={() =>
                                                this.deleteEmployee(employee.id)
                                            }
                                            className="btn btn-danger me-2"
                                        >
                                            Delete
                                        </button>
                                        <button
                                            onClick={() =>
                                                this.viewEmployee(employee.id)
                                            }
                                            className="btn btn-info"
                                        >
                                            View
                                        </button>
                                    </td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        );
    }
}

    export default ListEmployeeComponent
